import React, { useEffect, useRef, useState } from "react";
import './note.css'
import Editor from "../pages/Editor";
import SingleNote from "./SingleNote";

function Note() {
  const [notes, setNotes] = useState([]);
  const saveNote = (text,id) => {
    console.log(text);

    if(id<notes.length)
    {
      const editNotes=notes;
      notes[id]={
        id:id,
        data:text
      }
      setNotes([ ...editNotes ]);
    }
    else{
      const id=notes.length;
      const note={
        id:id,
        data:text
      }

      console.log("notes",notes)
    }


  };

  let noteList=notes.map((note,id)=> (<SingleNote key={id+1} id={note.id} note={note.data} saveNote={saveNote}/>));



  return (
    <div className="notesContainer">
      <Editor className="noteEditor" saveNote={saveNote} />
      <h2>Notes List</h2>
      <div className="noteList">  
         {noteList}
         </div>
    </div>
  );
}

export default Note;
